sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
  const invalidNumbers = api.state.invalid_sourcing_numbers;
  let message = '';
  if (api.state.all_are_invalid)
    message = 'All of the requests you selected are already engaged with a supplier or are inactive. Remove these requests from the negotiation event and try again.';
  else
    message = 'One or more of the selected sourcing requests are not eligible and will not be added: {' + invalidNumbers.toString() + '}';

	return message;
}
