sys_ux_client_script.required_translations:
[
    {
        "message": "One or more of the selected sourcing requests are not eligible and will not be added: {0}"
    },
    {
        "message": "All of the requests you selected are already engaged with a supplier or are inactive. Remove these requests from the negotiation event and try again."
    }
]

sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
async function handler({api, event, helpers, imports}) {
    const validSourcingIds = event.payload.data.output.valid_ids.map((sourcingObj) => sourcingObj.id);
    const invalidSourcingNumbers = event.payload.data.output.invalid_ids.map((sourcingObj) => sourcingObj.number);
    api.setState("valid_sourcing_ids", validSourcingIds);
    api.setState("invalid_sourcing_numbers", invalidSourcingNumbers);
    let message = '';
    if (validSourcingIds.length > 0) {
        api.data.get_negotiation_events.execute({
            "inputText" : ""
        });
        message = await helpers.translate('One or more of the selected sourcing requests are not eligible and will not be added: {0}', '{' + invalidSourcingNumbers.toString() + '}');
    }
    else {
        api.setState("all_are_invalid", true);
        message = await helpers.translate('All of the requests you selected are already engaged with a supplier or are inactive. Remove these requests from the negotiation event and try again.');
    }
    api.setState('message', message);
}
